<?php

namespace Denis\UserBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * AdRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class AdRepository extends EntityRepository
{

	public function repeatCity($container, $location)
	{
		$user = $container->get('security.context')->getToken()->getUser();
		
		$qm = $this->createQueryBuilder('a')
				->where('a.location = :location AND a.user = :user')
				->setParameter('location', $location)
				->setParameter('user', $user)
				->getQuery()
				->getResult();
		
		// If the user have not by an ad for this location it returns 0 This is for the ValidLocationValidator.php		
		return count($qm);
	}
	


}
