<?phpnamespace Denis\UserBundle\Validator\Constraints;use Symfony\Component\Validator\Constraint;use Symfony\Component\Validator\ConstraintValidator;class ValidDurationValidator extends ConstraintValidator{    public function validate($value, Constraint $constraint)    {		$getID3 = new \getID3;		$file = $getID3->analyze($value);		$duration = floor($file["quicktime"]["moov"]["subatoms"][0]["duration"] / 1000);		//If movie duration is more than 10s error		if($duration > 10)		{			$this->context->addViolation(                $constraint->message,                array('%string%' => $duration)            );		}		    }}